@import 'reset';
@import 'variables';
@import 'home';

* {
  box-sizing: border-box;
}

body,
html {
  font-size: 18px;
  background: $color1;
  font-family: 'Barlow Semi Condensed', sans-serif;
  height: 100vh;
}

.primary-btn {
  text-decoration: none;
  background: $color1;
  -webkit-border-radius: 30px;
  -moz-border-radius: 30px;
  border-radius: 30px;
  text-align: center;
  color: $color3;
  padding: 15px 30px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 2.5px;
  cursor: pointer;
  margin-right: 5px;
  transition: all 0.2s ease-in-out;
  &:hover,
  &:focus {
    background: $hover;
  }
}

.ghost-btn {
  text-decoration: none;
  border: 4px solid $color1;
  display: inline-block;
  -webkit-border-radius: 30px;
  -moz-border-radius: 30px;
  border-radius: 30px;
  text-align: center;
  color: $color1;
  padding: 12px 27px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 2.5px;
  transition: all 0.2s ease-in-out;
  cursor: pointer;
  &:hover,
  &:focus {
    border-color: $hover;
  }
}

.menu-icon {
  font-size: 2rem;
  color: $color1;
  position: fixed;
  right: 36px;
  top: 36px;
  padding: 20px;
}

#menu {
  background: $color1;
  position: fixed;
  width: 100%;
  transform: translate3d(-100%, 0, 0);
  top: 0;
  bottom: 0;
  z-index: 10;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 1;
  transition: all 0.5s ease-in-out;

  nav {
    width: 400px;

    a {
      display: block;
      text-align: center;
      font-size: 2.3rem;
      padding: 1rem;
      text-decoration: none;
      color: $color3;
      &:hover {
        color: $color4;
      }
    }
  }
}

.menu-active {
  .menu-icon {
    z-index: 11;
    .fa {
      color: $color3;
    }
  }

  #menu {
    width: 100%;
    height: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(0, 0, 0);
  }
}
